const http = require('http');
const fs = require('fs');

const server = http.createServer((request, response) => {
  if (request.method === 'GET' && request.url === '/video') {
    // Set headers to indicate that the response is a video
    response.writeHead(200, {
      'Content-Type': 'video/mp4',
    });

    // Stream the video file to the response
    const videoStream = fs.createReadStream('path/to/video.mp4');
    videoStream.pipe(response);
  } else {
    // Return a 404 error for all other requests
    response.writeHead(404);
    response.end();
  }
});

server.listen(8080, () => {
  console.log('Server listening on port 8080');
});